import React from 'react';
import { Link } from 'react-router-dom';
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  CardActionArea,
  Box,
} from '@mui/material';
import PropTypes from 'prop-types';

const BlogCard = ({ blog }) => {
  const defaultCoverImage = '/placeholder-blog.jpg';

  return (
    <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      <CardActionArea component={Link} to={`/blogs/${blog._id}`}>
        <CardMedia
          component="img"
          height="200"
          image={blog.coverImage ? `http://localhost:5000${blog.coverImage}` : defaultCoverImage}
          alt={blog.title}
          sx={{ objectFit: 'cover' }}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2" noWrap>
            {blog.title}
          </Typography>
          <Typography
            variant="body2"
            color="text.secondary"
            sx={{
              overflow: 'hidden',
              textOverflow: 'ellipsis',
              display: '-webkit-box',
              WebkitLineClamp: 3,
              WebkitBoxOrient: 'vertical',
            }}
          >
            {blog.content}
          </Typography>
          <Box sx={{ mt: 2 }}>
            <Typography variant="caption" color="text.secondary">
              By {blog.author?.name || 'Unknown'} â€¢ {new Date(blog.createdAt).toLocaleDateString()}
            </Typography>
          </Box>
        </CardContent>
      </CardActionArea>
    </Card>
  );
};

BlogCard.propTypes = {
  blog: PropTypes.shape({
    _id: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired,
    content: PropTypes.string.isRequired,
    coverImage: PropTypes.string,
    author: PropTypes.shape({
      name: PropTypes.string,
    }),
    createdAt: PropTypes.string.isRequired,
  }).isRequired,
};

export default BlogCard;
