import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link as RouterLink } from 'react-router-dom';
import {
  Container,
  Paper,
  Typography,
  Button,
  Box,
  Divider,
  ImageList,
  ImageListItem,
} from '@mui/material';
import { blogAPI } from '../services/api';

const BlogDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [blog, setBlog] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        if (!id) return;
        const blogData = await blogAPI.getBlog(id);
        setBlog(blogData);
      } catch (err) {
        setError(err.response?.data?.message || 'An error occurred');
      } finally {
        setLoading(false);
      }
    };

    fetchBlog();
  }, [id]);

  const handleDelete = async () => {
    try {
      if (!id) return;
      await blogAPI.deleteBlog(id);
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'An error occurred');
    }
  };

  if (loading) {
    return (
      <Container>
        <Typography>Loading...</Typography>
      </Container>
    );
  }

  if (error) {
    return (
      <Container>
        <Typography color="error">{error}</Typography>
      </Container>
    );
  }

  if (!blog) {
    return (
      <Container>
        <Typography>Blog not found</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 4 }}>
        <Paper sx={{ p: 4 }}>
          {/* Cover Image */}
          {blog.coverImage && (
            <Box sx={{ mb: 4, borderRadius: 1, overflow: 'hidden' }}>
              <img
                src={`http://localhost:5000${blog.coverImage}`}
                alt={blog.title}
                style={{
                  width: '100%',
                  height: '400px',
                  objectFit: 'cover',
                }}
              />
            </Box>
          )}

          <Typography variant="h4" component="h1" gutterBottom>
            {blog.title}
          </Typography>
          <Typography variant="subtitle1" color="text.secondary" gutterBottom>
            By {blog.author.name} • {new Date(blog.createdAt).toLocaleDateString()}
          </Typography>
          <Divider sx={{ my: 2 }} />
          
          {/* Blog Content */}
          <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap', mb: 4 }}>
            {blog.content}
          </Typography>

          {/* Content Images */}
          {blog.contentImages && blog.contentImages.length > 0 && (
            <Box sx={{ mt: 4 }}>
              <Typography variant="h6" gutterBottom>
                Additional Images
              </Typography>
              <ImageList sx={{ width: '100%' }} cols={2} gap={16}>
                {blog.contentImages.map((image, index) => (
                  <ImageListItem key={index}>
                    <img
                      src={`http://localhost:5000${image}`}
                      alt={`Blog content ${index + 1}`}
                      loading="lazy"
                      style={{
                        width: '100%',
                        height: '300px',
                        objectFit: 'cover',
                        borderRadius: '4px',
                      }}
                    />
                  </ImageListItem>
                ))}
              </ImageList>
            </Box>
          )}

          {/* Action Buttons */}
          {token && blog.author._id === JSON.parse(atob(token.split('.')[1])).user.id && (
            <Box sx={{ mt: 4 }}>
              <Button
                variant="contained"
                color="primary"
                component={RouterLink}
                to={`/blogs/edit/${blog._id}`}
                sx={{ mr: 2 }}
              >
                Edit
              </Button>
              <Button
                variant="contained"
                color="error"
                onClick={handleDelete}
              >
                Delete
              </Button>
            </Box>
          )}
        </Paper>
      </Box>
    </Container>
  );
};

export default BlogDetail;
