import axios, { InternalAxiosRequestConfig } from 'axios';
import { LoginCredentials, SignupCredentials, BlogFormData } from '../types';

const API_URL = 'http://localhost:5000/api';

// Create axios instance with base URL
const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add token to requests if it exists
api.interceptors.request.use((config: InternalAxiosRequestConfig) => {
  const token = localStorage.getItem('token');
  if (token && config.headers) {
    config.headers['x-auth-token'] = token;
  }
  return config;
});

export const authAPI = {
  login: async (credentials: LoginCredentials) => {
    const response = await api.post('/auth/login', credentials);
    return response.data;
  },

  signup: async (credentials: SignupCredentials) => {
    const response = await api.post('/auth/signup', credentials);
    return response.data;
  },

  getUser: async () => {
    const response = await api.get('/auth/user');
    return response.data;
  },
};

export const blogAPI = {
  getBlogs: async (page = 1, limit = 10) => {
    const response = await api.get(`/blogs?page=${page}&limit=${limit}`);
    return response.data;
  },

  getBlog: async (id: string) => {
    const response = await api.get(`/blogs/${id}`);
    return response.data;
  },

  createBlog: async (blogData: BlogFormData) => {
    const response = await api.post('/blogs', blogData);
    return response.data;
  },

  updateBlog: async (id: string, blogData: BlogFormData) => {
    const response = await api.put(`/blogs/${id}`, blogData);
    return response.data;
  },

  deleteBlog: async (id: string) => {
    const response = await api.delete(`/blogs/${id}`);
    return response.data;
  },
};
